import React, { useState, useCallback, useEffect } from 'react';
import { ChevronLeft, RotateCcw, Play, BookOpen, Target } from 'lucide-react';

const ChessTrainingApp = () => {
  // Inline styles
  const styles = {
    appContainer: {
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #fef7cd 0%, #fed7aa 100%)',
      padding: '20px',
      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", "Roboto", sans-serif'
    },
    container: {
      maxWidth: '1200px',
      margin: '0 auto'
    },
    header: {
      textAlign: 'center',
      marginBottom: '40px'
    },
    headerTitle: {
      fontSize: '2.5rem',
      color: '#92400e',
      marginBottom: '10px',
      fontWeight: 'bold'
    },
    headerSubtitle: {
      color: '#a16207',
      fontSize: '1.1rem'
    },
    openingCards: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
      gap: '30px',
      marginBottom: '40px'
    },
    openingCard: {
      background: 'white',
      borderRadius: '12px',
      padding: '30px',
      boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
      transition: 'box-shadow 0.3s ease'
    },
    cardHeader: {
      display: 'flex',
      alignItems: 'center',
      marginBottom: '20px',
      fontSize: '1.5rem',
      fontWeight: '600'
    },
    cardDescription: {
      color: '#6b7280',
      marginBottom: '20px',
      lineHeight: '1.6'
    },
    cardButton: {
      width: '100%',
      padding: '12px 20px',
      border: 'none',
      borderRadius: '8px',
      fontWeight: '600',
      fontSize: '1rem',
      cursor: 'pointer',
      transition: 'background-color 0.3s ease',
      color: 'white'
    },
    gameContainer: {
      maxWidth: '1400px',
      margin: '0 auto'
    },
    gameHeader: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      marginBottom: '30px',
      flexWrap: 'wrap',
      gap: '20px'
    },
    navButton: {
      display: 'flex',
      alignItems: 'center',
      background: 'none',
      border: 'none',
      color: '#a16207',
      fontSize: '1rem',
      cursor: 'pointer',
      transition: 'color 0.3s ease'
    },
    gameTitle: {
      textAlign: 'center'
    },
    gameTitleH2: {
      fontSize: '2rem',
      color: '#92400e',
      marginBottom: '5px'
    },
    gameTitleP: {
      color: '#a16207'
    },
    gameLayout: {
      display: 'grid',
      gridTemplateColumns: '1fr 350px',
      gap: '30px'
    },
    boardSection: {
      display: 'flex',
      justifyContent: 'center'
    },
    chessBoard: {
      display: 'grid',
      gridTemplateColumns: 'repeat(8, 1fr)',
      gap: '0',
      width: '480px',
      height: '480px',
      border: '3px solid #92400e',
      borderRadius: '8px',
      overflow: 'hidden',
      boxShadow: '0 8px 25px rgba(0, 0, 0, 0.15)'
    },
    chessSquare: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontSize: '2.5rem',
      cursor: 'pointer',
      transition: 'all 0.2s ease',
      userSelect: 'none'
    },
    sidebar: {
      display: 'flex',
      flexDirection: 'column',
      gap: '20px'
    },
    infoCard: {
      background: 'white',
      borderRadius: '12px',
      padding: '20px',
      boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
    },
    infoCardTitle: {
      fontSize: '1.2rem',
      fontWeight: '600',
      marginBottom: '15px',
      color: '#374151'
    },
    feedback: {
      borderRadius: '12px',
      padding: '20px',
      fontWeight: '600'
    },
    howItWorks: {
      background: 'white',
      borderRadius: '12px',
      padding: '30px',
      boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
    },
    features: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
      gap: '20px'
    },
    moveHistory: {
      maxHeight: '200px',
      overflowY: 'auto',
      fontSize: '0.9rem'
    },
    moveEntry: {
      display: 'flex',
      marginBottom: '4px'
    }
  };

  // Initial board setup
  const initialBoard = [
    ['r', 'n', 'b', 'q', 'k', 'b', 'n', 'r'],
    ['p', 'p', 'p', 'p', 'p', 'p', 'p', 'p'],
    [null, null, null, null, null, null, null, null],
    [null, null, null, null, null, null, null, null],
    [null, null, null, null, null, null, null, null],
    [null, null, null, null, null, null, null, null],
    ['P', 'P', 'P', 'P', 'P', 'P', 'P', 'P'],
    ['R', 'N', 'B', 'Q', 'K', 'B', 'N', 'R']
  ];

  const [board, setBoard] = useState(initialBoard);
  const [selectedSquare, setSelectedSquare] = useState(null);
  const [currentPlayer, setCurrentPlayer] = useState('white');
  const [moveHistory, setMoveHistory] = useState([]);
  const [gameMode, setGameMode] = useState('menu');
  const [trainingMode, setTrainingMode] = useState(null);
  const [feedback, setFeedback] = useState('');
  const [score, setScore] = useState({ correct: 0, total: 0 });

  // Opening databases
  const openingDatabases = {
    sicilianDragon: {
      name: "Sicilian Defence - Accelerated Dragon",
      color: "black",
      moves: {
        "": ["e2-e4"],
        "e2-e4": ["c7-c5"],
        "e2-e4,c7-c5": ["g1-f3"],
        "e2-e4,c7-c5,g1-f3": ["g7-g6"],
        "e2-e4,c7-c5,g1-f3,g7-g6": ["d2-d4"],
        "e2-e4,c7-c5,g1-f3,g7-g6,d2-d4": ["c5-d4"],
        "e2-e4,c7-c5,g1-f3,g7-g6,d2-d4,c5-d4": ["f3-d4"],
        "e2-e4,c7-c5,g1-f3,g7-g6,d2-d4,c5-d4,f3-d4": ["f8-g7"],
        "e2-e4,c7-c5,g1-f3,g7-g6,d2-d4,c5-d4,f3-d4,f8-g7": ["c2-c4", "b1-c3", "f2-f3"],
        "e2-e4,c7-c5,g1-f3,g7-g6,d2-d4,c5-d4,f3-d4,f8-g7,c2-c4": ["b8-c6"],
        "e2-e4,c7-c5,g1-f3,g7-g6,d2-d4,c5-d4,f3-d4,f8-g7,b1-c3": ["b8-c6"],
      }
    },
    scotchGame: {
      name: "Scotch Game",
      color: "white",
      moves: {
        "": ["e2-e4"],
        "e2-e4": ["e7-e5"],
        "e2-e4,e7-e5": ["g1-f3"],
        "e2-e4,e7-e5,g1-f3": ["b8-c6"],
        "e2-e4,e7-e5,g1-f3,b8-c6": ["d2-d4"],
        "e2-e4,e7-e5,g1-f3,b8-c6,d2-d4": ["e5-d4"],
        "e2-e4,e7-e5,g1-f3,b8-c6,d2-d4,e5-d4": ["f3-d4"],
        "e2-e4,e7-e5,g1-f3,b8-c6,d2-d4,e5-d4,f3-d4": ["f8-c5", "g8-f6", "d8-h4"],
        "e2-e4,e7-e5,g1-f3,b8-c6,d2-d4,e5-d4,f3-d4,f8-c5": ["d4-b5", "d4-c6"],
        "e2-e4,e7-e5,g1-f3,b8-c6,d2-d4,e5-d4,f3-d4,g8-f6": ["d4-c6", "b1-c3"],
      }
    },
    scotchGambit: {
      name: "Scotch Gambit",
      color: "white",
      moves: {
        "": ["e2-e4"],
        "e2-e4": ["e7-e5"],
        "e2-e4,e7-e5": ["g1-f3"],
        "e2-e4,e7-e5,g1-f3": ["b8-c6"],
        "e2-e4,e7-e5,g1-f3,b8-c6": ["d2-d4"],
        "e2-e4,e7-e5,g1-f3,b8-c6,d2-d4": ["e5-d4"],
        "e2-e4,e7-e5,g1-f3,b8-c6,d2-d4,e5-d4": ["f1-c4"],
        "e2-e4,e7-e5,g1-f3,b8-c6,d2-d4,e5-d4,f1-c4": ["f8-c5", "g8-f6", "f7-f5"],
        "e2-e4,e7-e5,g1-f3,b8-c6,d2-d4,e5-d4,f1-c4,f8-c5": ["c2-c3", "e1-g1"],
        "e2-e4,e7-e5,g1-f3,b8-c6,d2-d4,e5-d4,f1-c4,g8-f6": ["e4-e5", "c2-c3"],
      }
    }
  };

  // Piece Unicode symbols
  const pieceSymbols = {
    'K': '♔', 'Q': '♕', 'R': '♖', 'B': '♗', 'N': '♘', 'P': '♙',
    'k': '♚', 'q': '♛', 'r': '♜', 'b': '♝', 'n': '♞', 'p': '♟'
  };

  // Helper functions
  const getSquareName = (row, col) => {
    return String.fromCharCode(97 + col) + (8 - row);
  };

  const parseSquareName = (square) => {
    const col = square.charCodeAt(0) - 97;
    const row = 8 - parseInt(square[1]);
    return [row, col];
  };

  const isPieceWhite = (piece) => piece && piece === piece.toUpperCase();
  const isPieceBlack = (piece) => piece && piece === piece.toLowerCase();

  const isValidMove = (fromRow, fromCol, toRow, toCol, piece) => {
    // Basic bounds checking
    if (toRow < 0 || toRow >= 8 || toCol < 0 || toCol >= 8) return false;

    // Can't capture own pieces
    const targetPiece = board[toRow][toCol];
    if (targetPiece && isPieceWhite(piece) === isPieceWhite(targetPiece)) return false;

    // Basic piece movement validation (simplified)
    const rowDiff = Math.abs(toRow - fromRow);
    const colDiff = Math.abs(toCol - fromCol);
    const piece_type = piece.toLowerCase();

    switch (piece_type) {
      case 'p':
        const direction = isPieceWhite(piece) ? -1 : 1;
        const startRow = isPieceWhite(piece) ? 6 : 1;

        if (toCol === fromCol) { // Forward move
          if (toRow === fromRow + direction && !targetPiece) return true;
          if (fromRow === startRow && toRow === fromRow + 2 * direction && !targetPiece) return true;
        } else if (Math.abs(toCol - fromCol) === 1 && toRow === fromRow + direction) {
          return !!targetPiece; // Capture
        }
        return false;

      case 'r':
        return (rowDiff === 0 || colDiff === 0) && !isPathBlocked(fromRow, fromCol, toRow, toCol);

      case 'n':
        return (rowDiff === 2 && colDiff === 1) || (rowDiff === 1 && colDiff === 2);

      case 'b':
        return rowDiff === colDiff && !isPathBlocked(fromRow, fromCol, toRow, toCol);

      case 'q':
        return (rowDiff === 0 || colDiff === 0 || rowDiff === colDiff) && !isPathBlocked(fromRow, fromCol, toRow, toCol);

      case 'k':
        return rowDiff <= 1 && colDiff <= 1;

      default:
        return false;
    }
  };

  const isPathBlocked = (fromRow, fromCol, toRow, toCol) => {
    const rowDir = toRow > fromRow ? 1 : toRow < fromRow ? -1 : 0;
    const colDir = toCol > fromCol ? 1 : toCol < fromCol ? -1 : 0;

    let currentRow = fromRow + rowDir;
    let currentCol = fromCol + colDir;

    while (currentRow !== toRow || currentCol !== toCol) {
      if (board[currentRow][currentCol]) return true;
      currentRow += rowDir;
      currentCol += colDir;
    }

    return false;
  };

  const makeMove = (fromRow, fromCol, toRow, toCol) => {
    const newBoard = board.map(row => [...row]);
    const piece = newBoard[fromRow][fromCol];

    newBoard[toRow][toCol] = piece;
    newBoard[fromRow][fromCol] = null;

    const moveNotation = `${getSquareName(fromRow, fromCol)}-${getSquareName(toRow, toCol)}`;

    setBoard(newBoard);
    setMoveHistory(prev => [...prev, moveNotation]);
    setCurrentPlayer(currentPlayer === 'white' ? 'black' : 'white');

    return moveNotation;
  };

  const handleSquareClick = (row, col) => {
    if (gameMode !== 'training') return;

    const piece = board[row][col];

    if (selectedSquare) {
      const [fromRow, fromCol] = selectedSquare;
      const fromPiece = board[fromRow][fromCol];

      if (row === fromRow && col === fromCol) {
        setSelectedSquare(null);
        return;
      }

      if (isValidMove(fromRow, fromCol, row, col, fromPiece)) {
        const userMove = makeMove(fromRow, fromCol, row, col);
        checkMove(userMove);
        setSelectedSquare(null);
      } else {
        setSelectedSquare(piece && isPieceWhite(piece) === (currentPlayer === 'white') ? [row, col] : null);
      }
    } else {
      if (piece && isPieceWhite(piece) === (currentPlayer === 'white')) {
        setSelectedSquare([row, col]);
      }
    }
  };

  const checkMove = (userMove) => {
    const opening = openingDatabases[trainingMode];
    const moveKey = moveHistory.slice(0, -1).join(',');
    const expectedMoves = opening.moves[moveKey] || [];

    if (expectedMoves.includes(userMove)) {
      setFeedback('✓ Correct move!');
      setScore(prev => ({ correct: prev.correct + 1, total: prev.total + 1 }));

      // AI response
      setTimeout(() => {
        const nextMoveKey = moveHistory.join(',');
        const aiMoves = opening.moves[nextMoveKey];
        if (aiMoves && aiMoves.length > 0) {
          const aiMove = aiMoves[Math.floor(Math.random() * aiMoves.length)];
          const [fromSquare, toSquare] = aiMove.split('-');
          const [fromRow, fromCol] = parseSquareName(fromSquare);
          const [toRow, toCol] = parseSquareName(toSquare);
          makeMove(fromRow, fromCol, toRow, toCol);
        }
        setFeedback('');
      }, 1000);
    } else {
      setFeedback(`✗ Not the best move. Try: ${expectedMoves.join(' or ')}`);
      setScore(prev => ({ correct: prev.correct, total: prev.total + 1 }));
    }
  };

  const startTraining = (mode) => {
    setTrainingMode(mode);
    setGameMode('training');
    setBoard(initialBoard);
    setMoveHistory([]);
    setCurrentPlayer('white');
    setSelectedSquare(null);
    setFeedback('');
    setScore({ correct: 0, total: 0 });
  };

  const resetGame = () => {
    setBoard(initialBoard);
    setMoveHistory([]);
    setCurrentPlayer('white');
    setSelectedSquare(null);
    setFeedback('');
  };

  const backToMenu = () => {
    setGameMode('menu');
    setTrainingMode(null);
  };

  if (gameMode === 'menu') {
    return (
      <div style={styles.appContainer}>
        <div style={styles.container}>
          <div style={styles.header}>
            <h1 style={styles.headerTitle}>Chess Opening Trainer</h1>
            <p style={styles.headerSubtitle}>Master your favorite openings against AI opposition</p>
          </div>

          <div style={styles.openingCards}>
            <div style={styles.openingCard}>
              <div style={{...styles.cardHeader, color: '#dc2626'}}>
                <Target size={24} />
                <h2 style={{marginLeft: '10px'}}>Sicilian Dragon</h2>
              </div>
              <p style={styles.cardDescription}>Practice the Accelerated Dragon variation as Black. Sharp, aggressive play with the fianchettoed bishop on g7.</p>
              <button
                style={{...styles.cardButton, backgroundColor: '#dc2626'}}
                onMouseOver={(e) => e.target.style.backgroundColor = '#b91c1c'}
                onMouseOut={(e) => e.target.style.backgroundColor = '#dc2626'}
                onClick={() => startTraining('sicilianDragon')}
              >
                Train as Black
              </button>
            </div>

            <div style={styles.openingCard}>
              <div style={{...styles.cardHeader, color: '#2563eb'}}>
                <BookOpen size={24} />
                <h2 style={{marginLeft: '10px'}}>Scotch Game</h2>
              </div>
              <p style={styles.cardDescription}>Learn the classical Scotch Game as White. Direct central play with quick development and tactical opportunities.</p>
              <button
                style={{...styles.cardButton, backgroundColor: '#2563eb'}}
                onMouseOver={(e) => e.target.style.backgroundColor = '#1d4ed8'}
                onMouseOut={(e) => e.target.style.backgroundColor = '#2563eb'}
                onClick={() => startTraining('scotchGame')}
              >
                Train as White
              </button>
            </div>

            <div style={styles.openingCard}>
              <div style={{...styles.cardHeader, color: '#16a34a'}}>
                <Play size={24} />
                <h2 style={{marginLeft: '10px'}}>Scotch Gambit</h2>
              </div>
              <p style={styles.cardDescription}>Master the aggressive Scotch Gambit as White. Sacrifice a pawn for rapid development and attacking chances.</p>
              <button
                style={{...styles.cardButton, backgroundColor: '#16a34a'}}
                onMouseOver={(e) => e.target.style.backgroundColor = '#15803d'}
                onMouseOut={(e) => e.target.style.backgroundColor = '#16a34a'}
                onClick={() => startTraining('scotchGambit')}
              >
                Train as White
              </button>
            </div>
          </div>

          <div style={styles.howItWorks}>
            <h3 style={styles.infoCardTitle}>How it works</h3>
            <div style={styles.features}>
              <div>
                <h4 style={{fontWeight: '600', marginBottom: '10px', color: '#374151'}}>🎯 Targeted Practice</h4>
                <p style={{color: '#6b7280', fontSize: '0.9rem', lineHeight: '1.5'}}>Focus on specific opening variations with move-by-move guidance.</p>
              </div>
              <div>
                <h4 style={{fontWeight: '600', marginBottom: '10px', color: '#374151'}}>🤖 AI Opposition</h4>
                <p style={{color: '#6b7280', fontSize: '0.9rem', lineHeight: '1.5'}}>Face realistic responses based on grandmaster games and theory.</p>
              </div>
              <div>
                <h4 style={{fontWeight: '600', marginBottom: '10px', color: '#374151'}}>📈 Track Progress</h4>
                <p style={{color: '#6b7280', fontSize: '0.9rem', lineHeight: '1.5'}}>Monitor your accuracy and improvement over time.</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  const opening = openingDatabases[trainingMode];

  return (
    <div className="app-container">
      <div className="game-container">
        <div className="game-header">
          <button onClick={backToMenu} className="nav-button">
            <ChevronLeft size={20} />
            Back to Menu
          </button>

          <div className="game-title">
            <h2>{opening.name}</h2>
            <p>Playing as {opening.color}</p>
          </div>

          <button onClick={resetGame} className="nav-button">
            <RotateCcw size={20} />
            Reset
          </button>
        </div>

        <div className="game-layout">
          <div className="board-section">
            <div className="chess-board">
              {board.map((row, rowIndex) =>
                row.map((piece, colIndex) => (
                  <div
                    key={`${rowIndex}-${colIndex}`}
                    className={`chess-square ${
                      (rowIndex + colIndex) % 2 === 0 ? 'light' : 'dark'
                    } ${
                      selectedSquare && selectedSquare[0] === rowIndex && selectedSquare[1] === colIndex ? 'selected' : ''
                    }`}
                    onClick={() => handleSquareClick(rowIndex, colIndex)}
                  >
                    {piece && pieceSymbols[piece]}
                  </div>
                ))
              )}
            </div>
          </div>

          <div className="sidebar">
            <div className="info-card">
              <h3>Game Status</h3>
              <p>Current turn: <span className="capitalize">{currentPlayer}</span></p>
              <p>Move: {Math.ceil(moveHistory.length / 2)}</p>
            </div>

            <div className="info-card">
              <h3>Score</h3>
              <p className="score">{score.correct}/{score.total}</p>
              <p className="accuracy">
                {score.total > 0 ? `${Math.round((score.correct / score.total) * 100)}% accuracy` : 'No moves yet'}
              </p>
            </div>

            {feedback && (
              <div className={`feedback ${feedback.includes('✓') ? 'correct' : 'incorrect'}`}>
                <p>{feedback}</p>
              </div>
            )}

            <div className="info-card">
              <h3>Move History</h3>
              <div className="move-history">
                {moveHistory.length === 0 ? (
                  <p className="no-moves">No moves yet</p>
                ) : (
                  moveHistory.map((move, index) => (
                    <div key={index} className="move-entry">
                      <span className="move-number">{Math.ceil((index + 1) / 2)}.</span>
                      <span className={index % 2 === 0 ? 'white-move' : 'black-move'}>
                        {move}
                      </span>
                    </div>
                  ))
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChessTrainingApp;
